mapper.update:
UPDATE
  t_member_water
SET
    water_status = #{newWaterStatus}

      ,third_pay_water = #{transactionId}

WHERE
    water_status = #{oldWaterStatus}
    and pay_water = #{payWater}

mapper.select:
SELECT tmw.id, tor.order_no orderNo, IFNULL(tmw.mobile_phone,'') mobilePhone, IFNULL(tmw.member_name,'') memberName, DATE_FORMAT(tor.create_time,'%Y-%m-%d %H:%i') createTime, DATE_FORMAT(tor.payment_time,'%Y-%m-%d %H:%i') paymentTime, tor.order_amount sellPrice, IFNULL(tmw.pay_water,'') payWater, IFNULL(tmw.third_pay_water,'') thirdPayWater, case tor.payment_method when 'integral' then '积分抵扣' when 'wechatPay' then '微信支付' else '' end paymentMethodName, case tmw.water_status when 1 then '待付款' when 2 then '成功' when 3 then '失败' else '' end waterStatusName FROM t_member_water tmw join t_order tor on tor.member_id = tmw.member_id and tor.pay_water = tmw.pay_water where tmw.`status` = 1
and tmw.mobile_phone = #{memberWaterDTO.mobilePhone,jdbcType=VARCHAR}
and tor.order_no = #{memberWaterDTO.orderNo,jdbcType=VARCHAR}
and tmw.pay_water = #{memberWaterDTO.payWater,jdbcType=VARCHAR}
AND tor.create_time >= #{memberWaterDTO.startCreateTime,jdbcType=TIMESTAMP}
AND tor.create_time <= #{memberWaterDTO.endCreateTime,jdbcType=TIMESTAMP}
ORDER BY tor.create_time desc
